contract Pausable is IPausable, SafeOwnable {
  bool private _paused;

  modifier whenNotPaused() {
    require(!_paused, "Paused");
    _;
  }

  constructor() {}

  function setPaused(bool _newPaused) external override onlyOwner {
    _paused = _newPaused;
    emit PausedChange(_newPaused);
  }

