contracts/SafeAccessControlEnumerableUpg.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity =0.8.7;

import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "./interfaces/ISafeAccessControlEnumerableUpgradeable.sol";

abstract contract SafeAccessControlEnumerableUpgradeable is
  ISafeAccessControlEnumerableUpgradeable,
  AccessControlEnumerableUpgradeable
{
  mapping(bytes32 => bytes32) private _roleToRoleAdminNominee;
  mapping(bytes32 => mapping(address => bool))
    private _roleToAccountToNominated;

  function __SafeAccessControlEnumerable_init() internal onlyInitializing {
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
  }

  function setRoleAdminNominee(bytes32 _role, bytes32 _roleAdminNominee)
    public
    virtual
    override
    onlyRole(getRoleAdmin(_role))
  {
    _setRoleAdminNominee(_role, _roleAdminNominee);
  }

  function acceptRoleAdmin(bytes32 _role)
    public
    virtual
    override
    onlyRole(_roleToRoleAdminNominee[_role])
  {
    _setRoleAdmin(_role, _roleToRoleAdminNominee[_role]);
    _setRoleAdminNominee(_role, 0x00);
  }

  function grantRole(bytes32 _role, address _account)
    public
    virtual
    override
    onlyRole(getRoleAdmin(_role))
  {
    _setRoleNominee(_role, _account, true);
  }

  function acceptRole(bytes32 _role) public virtual override {
    require(
      _roleToAccountToNominated[_role][_msgSender()],
      "msg.sender != role nominee"
    );
    _setRoleNominee(_role, _msgSender(), false);
    _grantRole(_role, _msgSender());
  }

  function revokeNomination(bytes32 _role, address _account)
    public
    virtual
    override
    onlyRole(getRoleAdmin(_role))
  {
    _setRoleNominee(_role, _account, false);
  }

  function getRoleAdminNominee(bytes32 _role)
    public
    view
    virtual
    override
    returns (bytes32)
  {
    return _roleToRoleAdminNominee[_role];
  }

  function isNominated(bytes32 _role, address _account)
    public
    view
    virtual
    override
    returns (bool)
  {
    return _roleToAccountToNominated[_role][_account];
  }

  function _setRoleAdminNominee(bytes32 _role, bytes32 _newRoleAdminNominee)
    internal
    virtual
  {
    emit RoleAdminNomineeUpdate(
      _roleToRoleAdminNominee[_role],
      _newRoleAdminNominee
    );
    _roleToRoleAdminNominee[_role] = _newRoleAdminNominee;
  }

  function _setRoleNominee(
    bytes32 _role,
    address _account,
    bool _nominationStatus
  ) internal virtual {
    _roleToAccountToNominated[_role][_account] = _nominationStatus;
    emit RoleNomineeUpdate(_role, _account, _nominationStatus);
  }

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
   */
  uint256[50] private __gap;
}

contracts/interfaces/ISafeAccessControlE.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity =0.8.7;

import "@openzeppelin/contracts-upgradeable/access/IAccessControlEnumerableUpgradeable.sol";

/**
 * @notice An extension of OpenZeppelin's
 * `AccessControlEnumerableUpgradeable.sol` contract that requires an
 * address/role to initially be nominated, and then accepted, before the role
 * or admin role is granted.
 * @dev Inheriting contract must call `__SafeAccessControlEnumerable_init()`
 * within their own externally exposed `initializer`.
 */
interface ISafeAccessControlEnumerableUpgradeable is
  IAccessControlEnumerableUpgradeable
{
  /**
   * @dev Emitted via `setRoleAdminNominee()` and `acceptRoleAdmin()`.
   * @param role The role an admin was nominated for
   * @param newRoleAdminNominee The role nominated as the new admin
   */
  event RoleAdminNomineeUpdate(bytes32 role, bytes32 newRoleAdminNominee);

  /**
   * @dev Emitted via `grantRole`, `acceptRole`, and `revokeNomination`.
   * @param role The role that an address's nomination status was changed for
   * @param account The nominee whose nomination status was changed
   * @param nominated Whether the address was nominated
   */
  event RoleNomineeUpdate(bytes32 role, address account, bool nominated);

  /**
   * @notice Nominates a role to be a role admin.
   * @dev Only callable by a member of the role's current role admin.
   * @param role The role a role admin is to be nominated for
   * @param adminRoleNominee The role admin to be nominated
   */
  function setRoleAdminNominee(bytes32 role, bytes32 adminRoleNominee)
    external;

  /**
   * @notice Accepts a role admin nomination.
   * @dev Only callable by a member of the role admin nominee.
   * @param role The role a role admin nomination is to be accepted for
   */
  function acceptRoleAdmin(bytes32 role) external;

  /**
   * @notice Accepts a role nomination.
   * @dev Only callable by the role nominee.
   * @param role The role of the nomination to be accepted
   */
  function acceptRole(bytes32 role) external;

  /**
   * @notice Revokes a role nomination.
   * @dev Only callable by a member of the role's current role admin.
   * @param role The role of the nomination to be revoked
   * @param account The address of the nomination to be revoked
   */
  function revokeNomination(bytes32 role, address account) external;

  /**
   * @param role The role to retrieve a role admin nominee for
   * @return The current role admin nominee of `role`
   */
  function getRoleAdminNominee(bytes32 role) external view returns (bytes32);

  /**
   * @param role The role to retrieve nomination status for
   * @param account The address to retrieve nomination status for
   * @return Whether `account` is nominated for `role`
   */
  function isNominated(bytes32 role, address account)
    external
    view
    returns (bool);
}

